(function($){function previewButton(elem){var url=elem.text();var htt=url.split(":")[0];var button=$("<button class='preview_button'>Preview</button>");button.click(function(){if(htt=="https"){openDialog(url)}else{window.open(url,"_blank")}});return button}function openDialog(url){var iframe=$('<iframe class="modal-iframe" src="'+url+'"  class="modal-iframe" marginwidth="0" marginheight="0" allowfullscreen></iframe>');var dialog=$("<div></div>").append(iframe).appendTo("body").dialog({autoOpen:true,modal:true,dialogClass:"recommended-resources",resizable:false,width:"90%",height:"90%",position:{my:"center",at:"center",of:window},close:function(){iframe.attr("src","");$(this).dialog("destroy").remove()}})}$(document).ready(function(){var $urls=$("td.views-field.views-field-field-source-url a");$.each($urls,function(){var _this=$(this);$(this).qtip({content:{text:previewButton(_this)},hide:{fixed:true,delay:300},position:{my:"left center",at:"right center",target:$(this)},style:{classes:"transparent-qtip",width:"auto"}})})})})(jQuery);
